function cvjs_getAutoCADColor(hexNumber){

	var colorNumber = 1;
	if (hexNumber.indexOf("#")==0) hexNumber = hexNumber.substring(1);

	hexNumber = hexNumber.toUpperCase();;
//window.alert(" first "+colorNumber+"  "+hexNumber);		
	
	switch(hexNumber) {
		case "000000":
			colorNumber = 0;
			break;
		case "FF0000":
			colorNumber = 1;
			break;
		case "FFF200":  // yellow
		case "FFFF00":
			colorNumber = 2;
			break;
		case "22B14C": // green
		case "00FF00":
			colorNumber = 3;
			break;
		case "00FFFF":
			colorNumber = 4;
			break;
		case "0000FF":
			colorNumber = 5;
			break;
		case "FF00FF":  // purple
		case "800080":
			colorNumber = 6;
			break;
		case "FFFFFF":
			colorNumber = 7;
			break;
		case "414141":
			colorNumber = 8;
			break;
		case "808080":
			colorNumber = 9;
			break;
		case "FF0000":
			colorNumber = 10;
			break;
		case "FFAAAA":
			colorNumber = 11;
			break;
		case "BD0000":
			colorNumber = 12;
			break;
		case "BD7E7E":
			colorNumber = 13;
			break;
		case "810000":
			colorNumber = 14;
			break;
		case "815656":
			colorNumber = 15;
			break;
		case "FF7500":  // orange
		case "FFA500":  // orange
			colorNumber = 30;
			break;
		case "808080":  // gray
		case "828282":
			colorNumber = 253;
			break;
		case "B97A57":   // brown
		case "814000":
			colorNumber = 34;
			break;
			
		default:
			colorNumber = 1;
	}

//window.alert(colorNumber);	
	
	return colorNumber;
	
}

function cvjs_get_dwg_scaled_value(n, page){

	var svgDx = cvjs_drawingCoordinates[page].SVG.upperRight.x - cvjs_drawingCoordinates[page].SVG.lowerLeft.x;
	var dwgDx = cvjs_drawingCoordinates[page].DWG.upperRight.x - cvjs_drawingCoordinates[page].DWG.lowerLeft.x;

	return (dwgDx/svgDx*n)
}


