			cvjs_SVGHyperLinkCSSAll[jj][4] = cvjs_SVGHyperLinkCSSAll_Org[jj][4];
			cvjs_SVGHyperLinkCSSAll[jj][5] = cvjs_SVGHyperLinkCSSAll_Org[jj][5];
			cvjs_SVGHyperLinkCSSAll[jj][6] = cvjs_SVGHyperLinkCSSAll_Org[jj][6];

			// 3.0.13c
