	function getUnique(a) {
	  var b = [a[0]], i, j, tmp;
	  for (i = 1; i < a.length; i++) {
		tmp = 1;
		for (j = 0; j < b.length; j++) {
		  if (a[i] == b[j]) {
			tmp = 0;
			break;
		  }
		}
		if (tmp) {
		  b.push(a[i]);
		}
	  }
	  return b;
	}




